import React, {useState, useContext, useE} from 'react';
import "./components.css";
import TransitionsModal from "./taskPopup";


export default function TaskLibrary(){

    useEffect(() => {
        getTasks({})
    },[]);

    const [tasks, setTasks] = useState()

    function getTasks(res){
        fetch("http://localhost:5000/tasks/get", {
            method: "GET"
        })
        .then((res)=> { return res.json();
        })
        .then((res) =>{
            setTasks(res)
            return console.log(res)
        },
        // (error) => {
        //     setClasses("error")
        // }
        )
    }


    return(
        <div>
            <h1>Task Library</h1>
            <table>
                <tr>
                    <th>Task Name</th>
                    <th>Time Earned</th>
                    <th>Max Time/Day</th>
                </tr>
                <tr>
                    <td>Lexia</td>
                    <td>15</td>
                    <td>15</td>
                </tr>
            </table>
            <TransitionsModal/>
        </div>

    )
}
