  const [token, setToken] = useState('');

  const writeToken = (token) => {
      setToken(token)
  }

  return (
      <LoginContext.Provider  value={{user,setUser, loggedIn, setLoggedIn, token, writeToken}}>
          {props.children}
      </LoginContext.Provider>
  
  )
}

const LoginConsumer = LoginContext.Consumer
